const Tinkerforge = require('tinkerforge');
const logger = require('./../Logger');
const db = require('./../DatabaseHandler/index');
const mClient = require('./../MessageHandler/index').mClient;

// The different kinds of Bricks that are available.
const TempBrick = require('./Devices/TempBrick');
const HumidityBrick = require('./Devices/HumidityBrick');
const AmbientLightBrick = require('./Devices/AmbientLightBrick');

// Connection values for Tinkerforge.
const HOST = 'localhost';
const PORT = 4225;
const ipcon = new Tinkerforge.IPConnection();

ipcon.connect(HOST, PORT,
    (error) => {
        logger.error('Tinkerforge is currently not reachable - Error: ' + error);
    }
);

ipcon.on(Tinkerforge.IPConnection.CALLBACK_CONNECTED, () => {
    new TempBrick(ipcon, mClient, 'dFs', db);
    new HumidityBrick(ipcon, mClient, 'deY', db);
    new AmbientLightBrick(ipcon, mClient, 'avN', db);
});

const loki = require('lokijs');

/**
 * Abstraction for the Database, can be easily replaced with MongoDB or other non SQL DBs.
 */
class DatabaseHandler {

    /**
     * Initializes the database and created the available collections.
     */
    constructor() {
        this.db = new loki('fog.db');

        this.db.addCollection('humidity');
        this.db.addCollection('temperature');
        this.db.addCollection('illuminance');
    }

    /**
     * Handles insertions into the Database.
     * @param {string} collection Into which collection data shall be inserted.
     * @param {number} value Numeric value.
     * @param {number} time Numeric value in unix time format.
     * @param {string} peer Name of the peer that the data belongs to. 
     */
    insert = (collection, value, time, peer) => {
        let generic = this.db.getCollection(collection);
        generic.insert({ value, time, peer });
    }

    /**
     * Handles data retrievals from the Database.
     * @param {string} collection From which collection data shall be received.
     * @param {string} peer Name of the peer that the data belongs to.
     * @param {number} limit Numeric value to limit the amount of requested data.
     */
    receive = (collection, peer, limit) => {
        let generic = this.db.getCollection(collection);
        let data = generic.chain().find({ peer }).simplesort('time', true).limit(limit).data();
        return data.reverse();
    }
}

module.exports = new DatabaseHandler();

const winston = require('winston');
const config = require('./config');

const customConfig = {
    levels: {
        error: 0,
        debug: 1,
        warn: 2,
        data: 3,
        info: 4,
        verbose: 5,
        silly: 6,
        custom: 7,
    },
    colors: {
        error: 'red',
        debug: 'blue',
        warn: 'yellow',
        data: 'grey',
        info: 'green',
        verbose: 'cyan',
        silly: 'magenta',
        custom: 'yellow',
    },
};

/**
 * @public
 * Logger class, which allows us to log with colors.
 */
class Logger {
    /**
     * @constructor
     * @description adds the color palette to the winston module.
     */
    constructor() {
        winston.addColors(customConfig.colors);
    }

    /**
     * @description initializes winston.
     * @returns {Logger}
     */
    init() {
        return winston.createLogger({
            levels: customConfig.levels,
            format: winston.format.combine(
                winston.format.colorize(),
                winston.format.simple(),
            ),
            transports: [
                new winston.transports.Console(),
            ],
            level: (config.verbose) ? 'custom' : 'info',
        });
    }

}

module.exports = new Logger().init();

